import React, { useState, useEffect } from 'react';
import AppointmentForm from './AppointmentForm';
import { Appointment, Department, Employee } from './types';

const Calendar: React.FC = () => {
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [popupAppointment, setPopupAppointment] = useState<Appointment | null>(null);
  const [isCreating, setIsCreating] = useState<boolean>(false);

  useEffect(() => {
    fetchAppointments();
    fetchDepartments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/appointments/');
      const data = await response.json();
      const appointmentsData = data.map((appointment: any) => ({
        ...appointment,
        start: new Date(appointment.start_datetime),
        end: new Date(appointment.end_datetime),
      }));
      setAppointments(appointmentsData);
      console.log('Appointments loaded:', appointmentsData);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await fetch('http://localhost:8000/api/departments/');
      const data = await response.json();
      if (Array.isArray(data)) {
        setDepartments(data);
        console.log('Departments loaded:', data);
      } else {
        console.error("Expected an array of departments, but got:", data);
      }
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchEmployees = async (departmentId: number) => {
    try {
      const response = await fetch(`http://localhost:8000/api/employees/by-department/${departmentId}/`);
      const data = await response.json();
      setEmployees(data);
      console.log('Employees loaded for department:', departmentId, data);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const handleAppointmentClick = (appointment: Appointment) => {
    setPopupAppointment(appointment);
    setIsCreating(false);
  };

  const handleCreateNewAppointment = () => {
    setIsCreating(true);
    setPopupAppointment({
      title: '',
      start: new Date(selectedDate.setHours(6, 0, 0, 0)),
      end: new Date(selectedDate.setHours(7, 0, 0, 0)),
      description: '',
      departmentId: 0,
      participants: [],
    });
    fetchEmployees(0);
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h1>Calendar</h1>
      {/* Add CalendarHeader and CalendarGrid components here */}
      {(popupAppointment || isCreating) && (
        <AppointmentForm
          appointment={popupAppointment}
          isCreating={isCreating}
          departments={departments}
          employees={employees}
          onSave={(appointment) => {
            // Save logic
          }}
          onDelete={(appointmentId) => {
            // Delete logic
          }}
          onCancel={() => { setPopupAppointment(null); setIsCreating(false); }}
          onParticipantAdd={(participantId) => {
            // Add participant logic
          }}
          onParticipantRemove={(participantId) => {
            // Remove participant logic
          }}
          fetchEmployees={fetchEmployees}
        />
      )}
      <button onClick={handleCreateNewAppointment} style={{ marginTop: '10px' }}>
        Create New Appointment
      </button>
    </div>
  );
};

export default Calendar;
