import React, { useState, useEffect } from 'react';

interface Appointment {
  id: number;
  title: string;
  start: Date;
  end: Date;
  description: string;
  departmentId: number;
  participants: number[];
}

interface Department {
  id: number;
  name: string;
}

interface Employee {
  id: number;
  name: string;
}

const Calendar: React.FC = () => {
  const [appointments, setAppointments] = useState<Appointment[]>([]);
  const [departments, setDepartments] = useState<Department[]>([]);
  const [employees, setEmployees] = useState<Employee[]>([]);
  const [selectedDate, setSelectedDate] = useState<Date>(new Date());
  const [popupAppointment, setPopupAppointment] = useState<Appointment | null>(null);
  const [isCreating, setIsCreating] = useState<boolean>(false);
  const [showParticipantSelector, setShowParticipantSelector] = useState<boolean>(false);

  useEffect(() => {
    fetchAppointments();
    fetchDepartments();
  }, []);

  const fetchAppointments = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/appointments/');
      const data = await response.json();
      const appointmentsData = data.map((appointment: any) => ({
        ...appointment,
        start: new Date(appointment.start_datetime),
        end: new Date(appointment.end_datetime),
      }));
      setAppointments(appointmentsData);
      navigateToClosestAppointmentDate(appointmentsData);
    } catch (error) {
      console.error("Error fetching appointments:", error);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await fetch('http://localhost:5000/api/departments/');
      const data = await response.json();
      console.log("Fetched departments:", data); // Debugging line
      if (Array.isArray(data)) {
        setDepartments(data);
      } else {
        console.error("Expected an array of departments, but got:", data);
      }
    } catch (error) {
      console.error("Error fetching departments:", error);
    }
  };

  const fetchEmployees = async (departmentId: number) => {
    try {
      const response = await fetch(`http://localhost:5000/api/employees/by-department/${departmentId}/`);
      const data = await response.json();
      setEmployees(data);
    } catch (error) {
      console.error("Error fetching employees:", error);
    }
  };

  const navigateToClosestAppointmentDate = (appointments: Appointment[]) => {
    if (appointments.length === 0) return;

    const today = new Date();
    let closestDate = appointments[0].start;
    let minDiff = Math.abs(today.getTime() - closestDate.getTime());

    appointments.forEach(appointment => {
      const diff = Math.abs(today.getTime() - appointment.start.getTime());
      if (diff < minDiff) {
        minDiff = diff;
        closestDate = appointment.start;
      }
    });

    setSelectedDate(closestDate);
  };

  const handleDateChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setSelectedDate(new Date(event.target.value));
  };

  const handlePreviousDay = () => {
    setSelectedDate(new Date(selectedDate.setDate(selectedDate.getDate() - 1)));
  };

  const handleNextDay = () => {
    setSelectedDate(new Date(selectedDate.setDate(selectedDate.getDate() + 1)));
  };

  const getAppointmentsForDay = (date: Date) => {
    return appointments.filter(appointment =>
      appointment.start.toDateString() === date.toDateString()
    );
  };

  const handleSaveAppointment = (appointment: Appointment) => {
    if (isCreating) {
      // Logic to create a new appointment
      setAppointments([...appointments, appointment]);
    } else {
      // Logic to update an existing appointment
      setAppointments(appointments.map(a => (a.id === appointment.id ? appointment : a)));
    }
    setPopupAppointment(null);
    setIsCreating(false);
  };

  const handleDeleteAppointment = (appointmentId: number) => {
    setAppointments(appointments.filter(a => a.id !== appointmentId));
    setPopupAppointment(null);
    setIsCreating(false);
  };

  const handleParticipantRemove = (participantId: number) => {
    if (popupAppointment) {
      setPopupAppointment({
        ...popupAppointment,
        participants: popupAppointment.participants.filter(id => id !== participantId),
      });
    }
  };

  const handleParticipantAdd = (participantId: number) => {
    if (popupAppointment) {
      setPopupAppointment({
        ...popupAppointment,
        participants: [...popupAppointment.participants, participantId],
      });
    }
  };

  const renderAppointments = (appointments: Appointment[]) => {
    return appointments.map((appointment, index) => {
      const startHour = appointment.start.getHours();
      const endHour = appointment.end.getHours();
      const startMinutes = appointment.start.getMinutes();
      const endMinutes = appointment.end.getMinutes();
      const durationInMinutes = (endHour * 60 + endMinutes) - (startHour * 60 + startMinutes);

      return (
        <div
          key={appointment.id}
          style={{
            position: 'absolute',
            top: `${(startHour - 6) * 50 + (startMinutes / 60) * 50 + 50}px`,
            height: `${(durationInMinutes / 60) * 50}px`,
            width: '100%',
            backgroundColor: 'lightblue',
            border: '1px solid blue',
            boxSizing: 'border-box',
            cursor: 'pointer',
            overflow: 'hidden',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 1 + index,
          }}
          onClick={() => {
            setPopupAppointment(appointment);
            setIsCreating(false);
            fetchEmployees(appointment.departmentId);
          }}
        >
          <span style={{ whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
            {appointment.title}
          </span>
        </div>
      );
    });
  };

  const appointmentsForDay = getAppointmentsForDay(selectedDate);

  return (
    <div style={{ display: 'flex', alignItems: 'flex-start' }}>
      <div style={{ marginRight: '10px', textAlign: 'right' }}>
        {Array.from({ length: 16 }).map((_, index) => (
          <div key={index} style={{ height: '50px', lineHeight: '50px' }}>
            {index + 6}:00
          </div>
        ))}
      </div>
      <div style={{ width: '500px', border: '1px solid #ccc', padding: '10px', position: 'relative', height: '850px' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '10px' }}>
          <button onClick={handlePreviousDay}>Previous Day</button>
          <input type="date" value={selectedDate.toISOString().split('T')[0]} onChange={handleDateChange} />
          <button onClick={handleNextDay}>Next Day</button>
        </div>
        <div style={{ position: 'relative', height: '800px', marginTop: '50px' }}>
          {Array.from({ length: 16 }).map((_, index) => (
            <div key={index} style={{ height: '50px', borderBottom: '1px solid #eee', position: 'relative' }}>
              {/* This div represents the hour line */}
            </div>
          ))}
          {renderAppointments(appointmentsForDay)}
        </div>
        <button onClick={() => { setIsCreating(true); setPopupAppointment({ id: Date.now(), title: '', start: new Date(), end: new Date(), description: '', departmentId: 0, participants: [] }); }} style={{ marginTop: '10px' }}>Create New Appointment</button>
      </div>
      {(popupAppointment || isCreating) && (
        <div style={{ marginLeft: '20px', backgroundColor: 'white', padding: '20px', border: '1px solid #ccc', zIndex: 1000 }}>
          <h3>{isCreating ? 'Create New Appointment' : 'Edit Appointment'}</h3>
          <form onSubmit={(e) => {
            e.preventDefault();
            const form = e.target as HTMLFormElement;
            const newAppointment: Appointment = {
              id: isCreating ? Date.now() : popupAppointment!.id,
              title: (form.elements.namedItem('title') as HTMLInputElement).value,
              start: new Date((form.elements.namedItem('start') as HTMLInputElement).value),
              end: new Date((form.elements.namedItem('end') as HTMLInputElement).value),
              description: (form.elements.namedItem('description') as HTMLInputElement).value,
              departmentId: parseInt((form.elements.namedItem('department') as HTMLSelectElement).value),
              participants: popupAppointment ? popupAppointment.participants : [],
            };
            handleSaveAppointment(newAppointment);
          }}>
            <div>
              <label>Title: <input name="title" defaultValue={popupAppointment?.title || ''} required /></label>
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <label>Start: <input name="start" type="datetime-local" defaultValue={popupAppointment ? popupAppointment.start.toISOString().slice(0, 16) : ''} required /></label>
              <label>End: <input name="end" type="datetime-local" defaultValue={popupAppointment ? popupAppointment.end.toISOString().slice(0, 16) : ''} required /></label>
            </div>
            <div>
              <label>Department: 
                <select name="department" onChange={(e) => fetchEmployees(parseInt(e.target.value))} defaultValue={popupAppointment?.departmentId || ''} required>
                  <option value="" disabled>Select a department</option>
                  {departments.map(dept => (
                    <option key={dept.id} value={dept.id}>{dept.name}</option>
                  ))}
                </select>
              </label>
            </div>
            <div>
              <label>Participants:</label>
              <ul>
                {popupAppointment?.participants.map(participantId => {
                  const participant = employees.find(emp => emp.id === participantId);
                  return participant ? (
                    <li key={participant.id}>
                      {participant.name}
                      <button type="button" onClick={() => handleParticipantRemove(participant.id)}>Remove</button>
                    </li>
                  ) : null;
                })}
              </ul>
            </div>
            <div>
              <button type="button" onClick={() => setShowParticipantSelector(!showParticipantSelector)}>
                {showParticipantSelector ? 'Hide Participant Selector' : 'Add Participant'}
              </button>
              {showParticipantSelector && (
                <select onChange={(e) => {
                  const participantId = parseInt(e.target.value);
                  if (!popupAppointment?.participants.includes(participantId)) {
                    handleParticipantAdd(participantId);
                  }
                }}>
                  <option value="" disabled>Select a participant</option>
                  {employees.map(emp => (
                    <option key={emp.id} value={emp.id}>{emp.name}</option>
                  ))}
                </select>
              )}
            </div>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px' }}>
              <button type="submit">{isCreating ? 'Create' : 'Save'}</button>
              {!isCreating && <button type="button" onClick={() => handleDeleteAppointment(popupAppointment!.id)}>Delete</button>}
              <button type="button" onClick={() => { setPopupAppointment(null); setIsCreating(false); }}>Cancel</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default Calendar;
