export interface Appointment {
    id?: number;  // Make id optional to handle new appointments
    title: string;
    start: Date;
    end: Date;
    description: string;
    departmentId: number;
    participants: number[];
  }
  
  export interface Department {
    id: number;
    name: string;
  }
  
  export interface Employee {
    id: number;
    name: string;
  }
