import React, { useEffect, useState } from 'react';
import { Appointment, Department, Employee } from './types';

interface AppointmentFormProps {
  appointment: Appointment | null;
  isCreating: boolean;
  departments: Department[];
  employees: Employee[];
  onSave: (appointment: Appointment) => void;
  onDelete: (appointmentId: number) => void;
  onCancel: () => void;
  onParticipantAdd: (participantId: number) => void;
  onParticipantRemove: (participantId: number) => void;
  fetchEmployees: (departmentId: number) => void;
}

const AppointmentForm: React.FC<AppointmentFormProps> = ({
  appointment,
  isCreating,
  departments = [],
  employees = [],
  onSave,
  onDelete,
  onCancel,
  onParticipantAdd,
  onParticipantRemove,
  fetchEmployees,
}) => {
  const [localAppointment, setLocalAppointment] = useState<Appointment | null>(null);

  useEffect(() => {
    if (appointment) {
      setLocalAppointment(appointment);
    }
  }, [appointment]);

  const handleSave = (e: React.FormEvent) => {
    e.preventDefault();
    if (!localAppointment) return;

    const form = e.target as HTMLFormElement;
    const updatedAppointment: Appointment = {
      ...localAppointment,
      title: (form.elements.namedItem('title') as HTMLInputElement).value,
      start: new Date((form.elements.namedItem('start') as HTMLInputElement).value),
      end: new Date((form.elements.namedItem('end') as HTMLInputElement).value),
      description: (form.elements.namedItem('description') as HTMLInputElement).value,
      departmentId: parseInt((form.elements.namedItem('department') as HTMLSelectElement).value),
      participants: localAppointment.participants,
    };
    onSave(updatedAppointment);
  };

  return (
    <div style={{ marginLeft: '20px', backgroundColor: 'white', padding: '20px', border: '1px solid #ccc', zIndex: 1000 }}>
      <h3>{isCreating ? 'Create New Appointment' : 'Edit Appointment'}</h3>
      <form onSubmit={handleSave}>
        <div>
          <label>Title: <input name="title" defaultValue={localAppointment?.title || ''} required /></label>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          <label>Start: <input name="start" type="datetime-local" defaultValue={localAppointment ? localAppointment.start.toISOString().slice(0, 16) : ''} required /></label>
          <label>End: <input name="end" type="datetime-local" defaultValue={localAppointment ? localAppointment.end.toISOString().slice(0, 16) : ''} required /></label>
        </div>
        <div>
          <label>Description: <textarea name="description" defaultValue={localAppointment?.description || ''} required /></label>
        </div>
        <div>
          <label>Department: 
            <select name="department" onChange={(e) => fetchEmployees(parseInt(e.target.value))} defaultValue={localAppointment?.departmentId || ''} required>
              <option value="" disabled>Select a department</option>
              {departments.map(dept => (
                <option key={dept.id} value={dept.id}>{dept.name}</option>
              ))}
            </select>
          </label>
        </div>
        <div>
          <label>Participants:</label>
          <ul>
            {localAppointment?.participants.map(participantId => {
              const participant = employees.find(emp => emp.id === participantId);
              return participant ? (
                <li key={participant.id}>
                  {participant.name}
                  <button type="button" onClick={() => onParticipantRemove(participant.id)}>Remove</button>
                </li>
              ) : null;
            })}
          </ul>
        </div>
        <div>
          <select onChange={(e) => {
            const participantId = parseInt(e.target.value);
            if (!localAppointment?.participants.includes(participantId)) {
              onParticipantAdd(participantId);
            }
          }}>
            <option value="" disabled>Select a participant</option>
            {employees.map(emp => (
              <option key={emp.id} value={emp.id}>{emp.name}</option>
            ))}
          </select>
        </div>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px' }}>
          <button type="submit">{isCreating ? 'Create' : 'Save'}</button>
          {!isCreating && localAppointment?.id !== undefined && (
            <button type="button" onClick={() => onDelete(localAppointment.id!)}>Delete</button>
          )}
          <button type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default AppointmentForm;
